con:config.script:
import com.eviware.soapui.support.XmlHolder //for using XML Holder
import com.eviware.soapui.impl.wsdl.testcase.WsdlTestRunContext

log.info "Hello, Start the scripting"    //log.info example

log.info context.expand('${#TestCase#Name}')  //context.expand example within test case


//getPropertyValue functionality using testRunner
log.info testRunner.testCase.getPropertyValue("Name")

log.info testRunner.testCase.testSuite.testCases["addEmployee"].getPropertyValue("id")

log.info testRunner.testCase.testSuite.getPropertyValue("age")


//setPropertyValue functionality
//testRunner.testCase.testSuite.testCases["getEmployeeDetails"].setPropertyValue("department", "AEIE")

//Project level property access
def project= testRunner.testCase.testSuite.project

log.info project.getPropertyValue("gender")

//Hit addEmployee Request with Properties from project,testSuite,testCase level
def reqXml=testRunner.testCase.testSuite.testCases["addEmployee"].testSteps["add"].getPropertyValue("Request")
def xmlAdd=new XmlHolder(reqXml)

def dept=testRunner.testCase.testSuite.testCases["addEmployee"].getPropertyValue("dept")
def id= testRunner.testCase.testSuite.testCases["addEmployee"].getPropertyValue("id")
def age= testRunner.testCase.testSuite.testCases["addEmployee"].getPropertyValue("age")
def empName= testRunner.testCase.testSuite.testCases["addEmployee"].getPropertyValue("name")

xmlAdd.setNodeValue("//typ:addEmployee/typ:name",empName)
xmlAdd.setNodeValue("//typ:addEmployee/typ:id",id)
xmlAdd.setNodeValue("//typ:addEmployee/typ:Department",dept)
xmlAdd.setNodeValue("//typ:addEmployee/typ:age",age)

def newAddXml= xmlAdd.getXml()

//Set this request in ADD test step from where have to trigger
testRunner.testCase.testSuite.testCases["addEmployee"].testSteps["add"].setPropertyValue("Request",newAddXml)



//Call the service or trigger by testStep.run(testRunner,context), context of addEmployee testcase should be passed as 'add' teststep which we are trying to run belongs to that testcase

def addTestStep= testRunner.testCase.testSuite.testCases["addEmployee"].testSteps["add"]
def contextaddEmployee= new WsdlTestRunContext(addTestStep)

addTestStep.run(testRunner,contextaddEmployee)



//Hit getEmployee & Generate the response.. And validate the response which added in the earlier step

def getReq=testRunner.testCase.testSuite.testCases["getEmployee"].testSteps["get"].getPropertyValue("Request")

def xmlGet= new XmlHolder(getReq)

def emp= testRunner.testCase.testSuite.testCases["addEmployee"].getPropertyValue("name")

xmlGet.setNodeValue("//typ:getEmployeeDetails/typ:employeeName", emp)

def newXmlGet= xmlGet.getXml()

testRunner.testCase.testSuite.testCases["getEmployee"].testSteps["get"].setPropertyValue("Request",newXmlGet)

def empGetStep= testRunner.testCase.testSuite.testCases["getEmployee"].testSteps["get"]

def contextGetEmp= new WsdlTestRunContext(empGetStep)

empGetStep.run(testRunner,contextGetEmp)


// Delete the Employee

def delEmp=testRunner.testCase.testSuite.testCases["deleteEmployee"].testSteps["delete"].getPropertyValue("Request")

def delEmpXml= new XmlHolder(delEmp)


delEmpXml.setNodeValue("//typ:deleteEmployee/typ:employeeName", emp)


def newDelXml= delEmpXml.getXml()

testRunner.testCase.testSuite.testCases["deleteEmployee"].testSteps["delete"].setPropertyValue("Request", newDelXml)

def delTestStep= testRunner.testCase.testSuite.testCases["deleteEmployee"].testSteps["delete"]

def delContext= new WsdlTestRunContext(delTestStep)

delTestStep.run(testRunner,delContext)


//Validate Response by Assertion

def res= testRunner.testCase.testSuite.testCases["getEmployee"].testSteps["get"].getPropertyValue("Response")

def resXml= new XmlHolder(res)

def resXmlNew=resXml.getNodeValue("//ns:id")

log.info resXmlNew
log.info id

assert id==resXmlNew
